import { useEffect, useState } from 'react';
import styled from 'styled-components';
import { getLivros } from '../servicos/livros'; // Importa a função para buscar livros
import Pesquisa from '../componentes/Pesquisa';
import UltimosLancamentos from '../componentes/UltimosLancamentos';

const AppContainer = styled.div`
      width: 100vw;
      height: 100vh;
      background-image: linear-gradient(90deg, #FFA07A 35%, #FA8072);
`;

const ResultadoContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
`;

const Resultado = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 20px 0;
    cursor: pointer;
    text-align: center;
    padding: 0 100px;
    p {
        width: 200px;
        color: #FFF;
    }
    img {
        width: 100px;
    }
    &:hover {
        border: 1px solid white;
    }
`;

const Titulo = styled.h2`
    color: #FFF;
    font-size: 36px;
    text-align: center;
    width: 100%;
    padding-top: 35px;
`;

function Home() {
  const [livros, setLivros] = useState([]);

  async function fetchLivros() {
    const livrosDaAPI = await getLivros();
    setLivros(livrosDaAPI);
  }

  useEffect(() => {
    fetchLivros();
  }, []);

  return (
    <AppContainer>
      <Pesquisa />
      <Titulo>Livros Disponíveis</Titulo>
      <ResultadoContainer>
        {livros.map(livro => (
          <Resultado key={livro.id} onClick={() => {/* Função para adicionar aos favoritos */}}>
            <img src={livroImg} alt="Livro" />
            <p>{livro.nome}</p>
          </Resultado>
        ))}
      </ResultadoContainer>
      <UltimosLancamentos />
    </AppContainer>
  );
}

export default Home;
